import { BaseApiError } from 'models';
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { ButtonProps, Container, DefaultButton } from '../../components';
import { FormInput } from '../../components/footer/style';
import {
  FormColumn,
  FormInputRow,
  FormLabel,
  FormMessage,
  FormRow,
  FormSection,
  FormTitle,
  FormWrapper,
  Img,
  ImgWrapper,
} from '../../components/form/style';
import { useAuth } from '../../contexts/Auth';
interface FormProps {
  small: boolean;
  lightBg: boolean;
  imgStart: boolean;
  lightTitle: boolean;
  errorLightBg: boolean;
  lightText: boolean;
  errorMessage: string;
  hasError?: boolean;
}

export const LoginComponent = ({
  small = false,
  big = true,
  bigFont = true,
  primary = true,
  type = 'submit',
  lightBg = true,
  imgStart = false,
  lightTitle = false,
  errorLightBg = true,
  hasError,
  lightText = false,
}: FormProps & ButtonProps) => {
  const { Login } = useAuth();
  const history = useHistory();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const data = {
        _id: email,
        password: password,
      };
      await Login(data as any);
      history.push('/user');
    } catch (error) {
      if ((error as BaseApiError).status === 401) {
        setError('Não foi possível realizar o Login');
      }
    }
  };

  const messageVariants = {
    hidden: { y: 30, opacity: 0 },
    animate: { y: 0, opacity: 1, transition: { delay: 0.2, duration: 0.4 } },
  };

  const formData = [
    {
      label: 'Email',
      value: email,
      onChange: (e: React.ChangeEvent<HTMLInputElement>) => setEmail(e.target.value),
      type: 'email',
    },
    {
      label: 'Senha',
      value: password,
      onChange: (e: React.ChangeEvent<HTMLInputElement>) => setPassword(e.target.value),
      type: 'password',
    },
  ];
  return (
    <FormSection lightBg={lightBg}>
      <Container>
        <FormRow>
          <FormColumn small={small}>
            <ImgWrapper imgStart={imgStart}>
              <Img
                src="data:image/svg+xml;base64,
              
              
              
              
              
              "
                alt="music"
              ></Img>
            </ImgWrapper>
          </FormColumn>
          <FormColumn small={small}>
            <FormTitle lightTitle={lightTitle}>Login</FormTitle>
            <FormWrapper lightText={lightText} onSubmit={handleSubmit}>
              {formData.map((el, index) => (
                <FormInputRow key={index}>
                  <FormLabel style={{ color: 'black' }}>{el.label}</FormLabel>
                  <FormInput
                    type={el.type}
                    placeholder={`Insira: ${el.label.toLocaleLowerCase()}`}
                    value={el.value}
                    onChange={el.onChange}
                  />
                </FormInputRow>
              ))}

              <DefaultButton primary type="submit">
                ENTRAR
              </DefaultButton>
            </FormWrapper>
            {error && (
              <FormMessage
                errorLightBg={errorLightBg}
                variants={messageVariants}
                initial="hidden"
                animate="animate"
                hasError={!!error}
              >
                {error}
              </FormMessage>
            )}
          </FormColumn>
        </FormRow>
      </Container>
    </FormSection>
  );
};
